     pie[2][1] = blacksum;

     pie[3][0] = "green"
     pie[3][1] = greensum;

     pie[4][0] = "red"
     pie[4][1] = redsum;

     pie[5][0] = "yellow"
     pie[5][1] = yellowsum;

     pie[6][0] = "blue";
     pie[6][1] = bluesum;

     return pie;


}

scatterParser(data){
    var scat= new Array(data.length +1);
    scat[0] = ["Education Coefficient","Grade Point Average"];
    
    for(var i=1; i<data.length +1; i ++){
        scat[i] = new Array(2);
        scat[i][0]= data[i-1].educationCoefficient;
        scat[i][1] = data[i-1].gradePointAverage;
    }
    console.log(scat.toString());
    return scat;
}

columnParser(data){
    var col = new Array(4);
    var bluesum=0,  brownsum=0, greensum=0;
    var i;
    
    for(i=0; i<4; i++){
        col[i] = new Array(2)
    }

    for(i=0; i<data.length; i++){
        //col[i] = new Array(2);
        console.log("[col ]testing "+data[i].name)

        if(data[i].eyeColor === "blue"){
            bluesum++;
        }
        if(data[i].eyeColor === "brown"){
           brownsum++;
       }
       if(data[i].eyeColor === "green"){
           greensum++;
       }
    }
    console.log("blue"+bluesum+"brown"+brownsum+"green"+greensum)
     col[0][0] = "Color"
     col[0][1] = "Number of people which has this color as favorite";
     
     col[1][0] = "brown"
     col[1][1] = brownsum;

     col[2][0] = "green"
     col[2][1] = greensum;

     col[3][0] = "blue";
     col[3][1] = bluesum;

     return col;
}

bubbleParser(data){
var bub = new Array(data.length +1);
var i;
bub[0] = new Array(5);
bub[0] = ['Name', 'Age', 'Education Coefficient', 'Gender', 'Balance']
for(i=0; i<data.length; i++){
    bub[i+1] = new Array(5);
    bub[i+1] = [data[i].name, data[i].age, data[i].educationCoefficient, data[i].gender, data[i].balance];

}
return bub;
}



