  constructor(props) {
    super(props);
    //this.handleSubmit = this.handleSubmit.bind(this);
    //this.isLoggedIn = false;
    this.state = {
      isLoggedIn: false
    }

  }

  onUpdate = (val) => {
    this.setState({
      isLoggedIn: val
