import React from 'react'
import NameForm from './NameForm'


export default class RendForm extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
          isLoggedIn: "false",
          update: (isLoggedIn) => {
            if( (isLoggedIn !== this.state.isLoggedIn)){
                this.props.onUpdate(isLoggedIn);
                this.setState({isLoggedIn: isLoggedIn});
            }

          }
        }
        this.state.update(this.state.isLoggedIn);

    
      }

      onUpdate = (val, val2) => {
          //if(val !== this.state.isLoggedIn )
            this.setState({ isLoggedIn: val})
       // if(val2 !== this.state.isUnvalidated)
            this.setState({ isUnvalidated: val2 })
      };

render(){
    this.state.update(this.state.isLoggedIn, this.state.isUnvalidated);
    console.log("[RendForm]isUnvalidated after update : "+this.state.isUnvalidated);
    if(this.state.isUnvalidated === "false"){
        return(
         <NameForm onUpdate={() => this.onUpdate} handleSubmit = {this.props.handleSubmit}/>
        )
      }
      else {
        return(
          <div>
        <p color="red" style={{
          position: 'absolute', left: '50%', 
          transform: 'translate(-50%, -50%)'}}>Wrong Credentials !</p>
        <NameForm onUpdate={this.onUpdate} handleSubmit = {this.props.handleSubmit}/>
        </div>
        )
      }

}

}
