import NdPage from './pages/2ndPage.js'
import NameForm from './pages/NameForm.js'


export default class App extends React.Component{
  
  constructor(props) {
    super(props);
    //this.handleSubmit = this.handleSubmit.bind(this);
    //this.isLoggedIn = false;
    this.state = {
      isLoggedIn: false
    }

  }

  onUpdate = (val) => {
    this.setState({
      isLoggedIn: val
    })
  };

render(){

  console.log("log in parent : "+this.state.isLoggedIn)
  if(!this.state.isLoggedIn){
    return (
      <NameForm onUpdate={this.onUpdate}/>
    )
  }
  else {
    console.log("log in parent2 : "+this.state.isLoggedIn)

    return (
      <NdPage/>
    )
  }
  }

}

