import React from 'react'
import NameForm from './NameForm'


export default class RendForm extends React.Component{

    constructor(props) {
        super(props);
        //this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
          isLoggedIn: "false",
          isUnvalidated: "false",
          update: (isLoggedIn, isUnvalidated) => {
            console.log("in update "+isLoggedIn, isUnvalidated);
            this.props.onUpdate(isLoggedIn, isUnvalidated);
            this.setState({isLoggedIn: isLoggedIn, isUnvalidated: isUnvalidated});
          }
        
        }
    
      }

      //probleme onUpdate
      onUpdate = (val, val2) => {
          if(val !== this.state.isLoggedIn )
            this.setState({ isLoggedIn: val})
        if(val2 !== this.state.isUnvalidated)
            this.setState({ isUnvalidated: val2 })
      };

render(){
    console.log("isUnvalidated : "+this.state.isUnvalidated);
    this.state.update(this.state.isLoggedIn, this.state.isUnvalidated);
    if(this.state.isUnvalidated === "false"){
        return(
         <NameForm onUpdate={() => this.onUpdate} handleSubmit = {this.props.handleSubmit}/>
        )
      }
      else {
        return(
          <div>
        <p color="red" style={{
          position: 'absolute', left: '50%', 
          transform: 'translate(-50%, -50%)'}}>Wrong Credentials !</p>
        <NameForm onUpdate={this.onUpdate} handleSubmit = {this.props.handleSubmit}/>
        </div>
        )
      }
}

}
