/*eslint-disable no-unused-expressions */
import React from 'react'
var sha256 = require('js-sha256');

class NameForm extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: false,
      update: (isLoggedIn) => {
        console.log("in update "+isLoggedIn);
        this.props.onUpdate(isLoggedIn);
        this.setState({isLoggedIn: isLoggedIn});
      }
    }
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  

  handleSubmit(e) {
    var log=false;
    var upd = this.state.update;
    //alert(this.username.value);
    //alert(sha256(this.password.value));
    fetch('http://localhost:3000/connexion', {
      method: 'POST',
      headers: { 'Accept': 'application/json', 'Content-type':'application/json' },
      body :JSON.stringify({
        'username': this.username.value, 
        'password': sha256(this.password.value)
      })
  })
  .then((response) => response.json())
  //If response is in json then in success
  .then()
  //If response is not in json then in error
  .catch((error) => {
      //Error 
      console.error(error);
  })
  
   setTimeout( () => {
    var Http = new XMLHttpRequest();
    var url='http://localhost:3000/connexion';
    Http.open("GET", url);
   Http.send();

    Http.onreadystatechange = function () {
      if(this.readyState === 4 && this.status === 200){
        console.log(Http.response);
        var json = JSON.parse(Http.response);
        console.log("json access before assignment "+json.access);
        log=json.access;
        upd(log);
      }
    };
    setTimeout(this.state.update(log),2000); 
   }
   , 350);
   
    e.preventDefault();
  }


  render() {
    
    return (
      <form style={{
        position: 'absolute', left: '50%', top: '50%',
        transform: 'translate(-50%, -50%)'
    }} onSubmit={this.handleSubmit}>
        <label>
          
          <input  style={{
        position: 'absolute', left: '50%',
        transform: 'translate(-50%, -50%)'
    }} placeholder="username" ref={(username) => this.username = username} /> <p></p>
          <input   placeholder="password" type="text" ref={(password) => {
                        this.password = password} 
          }
          />
        </label>
        <p></p>
        <p></p>
        <input style={{
        position: 'absolute', left: '50%',
        transform: 'translate(-50%, -50%)'
    }}type="submit" value="Submit" />
      </form>
    );
  }
}

export default NameForm;
