import React from 'react'
import Chart from 'react-google-charts'

class App2 extends React.Component {

pieParser(data){
    var pie = new Array(data.length +1);
    var bluesum=0,  brownsum=0, blacksum=0, greensum=0, redsum=0, yellowsum = 0;
    var i;
    pie.forEach(element => {
        element = new Array(2);
    });
    pie[data.length] = new Array(2);
    for(i=0; i<data.length; i++){
        pie[i] = new Array(2);
        console.log("testing "+data[i].name)

        if(data[i].favColor === "blue"){
            bluesum++;
        }
        if(data[i].favColor === "brown"){
           brownsum++;
       }
       if(data[i].favColor === "black"){
           blacksum++;
       }
       if(data[i].favColor === "green"){
           greensum++;
       }
       if(data[i].favColor === "red"){
           redsum++;
       }
       if(data[i].favColor === "yellow"){
           yellowsum++;
       }
    }
    console.log("blue"+bluesum+"red"+redsum+"brown"+brownsum+"black"+blacksum+"green"+greensum+"yellow"+yellowsum)
     pie[0][0] = "Color"
     pie[0][1] = "Number of people which has this color as favorite";
     
     pie[1][0] = "brown"
     pie[1][1] = brownsum;

     pie[2][0] = "black"
     pie[2][1] = blacksum;

     pie[3][0] = "green"
     pie[3][1] = greensum;

     pie[4][0] = "red"
     pie[4][1] = redsum;

     pie[5][0] = "yellow"
     pie[5][1] = yellowsum;

     pie[6][0] = "blue";
     pie[6][1] = bluesum;

     return pie;


}

scatterParser(data){
    var scat= new Array(data.length +1);
    scat[0] = ["Hours Studied","Grade Point Average"];
    
    for(var i=1; i<data.length +1; i ++){
        scat[i] = new Array(2);
        scat[i][0]= data[i-1].hoursStudied;
        scat[i][1] = data[i-1].gradePointAverage;
    }
    console.log(scat.toString());
    return scat;
}

columnParser(data){
    var col = new Array(4);
    var bluesum=0,  brownsum=0, greensum=0;
    var i;
    
    for(i=0; i<4; i++){
        col[i] = new Array(2)
    }

    for(i=0; i<data.length; i++){
        //col[i] = new Array(2);
        console.log("[col ]testing "+data[i].name)

        if(data[i].eyeColor === "blue"){
            bluesum++;
        }
        if(data[i].eyeColor === "brown"){
           brownsum++;
       }
       if(data[i].eyeColor === "green"){
           greensum++;
       }
    }
    console.log("blue"+bluesum+"brown"+brownsum+"green"+greensum)
     col[0][0] = "Color"
     col[0][1] = "Number of people which has this color as favorite";
     
     col[1][0] = "brown"
     col[1][1] = brownsum;

     col[2][0] = "green"
     col[2][1] = greensum;

     col[3][0] = "blue";
     col[3][1] = bluesum;

     return col;
}



render(){
    //var histo = new Array(6);
    var data = [
        {
            "_id": "5dc83cc5ad0e701a999caee9",
            "age": 28,
            "balance": "$8,650.61",
            "eyeColor": "blue",
            "name": "Nieves Willis",
            "gender": "male",
            "gradePointAverage": 11,
            "hoursStudied": 28,
            "favColor": "black"
          },
          {
            "_id": "5dc83cc5582e24d8037e6157",
            "age": 29,
            "balance": "$17,768.37",
            "eyeColor": "green",
            "name": "Dudley Whitley",
            "gender": "male",
            "gradePointAverage": 3,
            "hoursStudied": 0,
            "favColor": "blue"
          },
          {
            "_id": "5dc83cc56410e27b79ebfef2",
            "age": 26,
            "balance": "$6,994.63",
            "eyeColor": "blue",
            "name": "Jenkins Golden",
            "gender": "male",
            "gradePointAverage": 3,
            "hoursStudied": 24,
            "favColor": "blue"
          },
          {
            "_id": "5dc83cc55adcd2c3acf465b4",
            "age": 33,
            "balance": "$5,604.88",
            "eyeColor": "brown",
            "name": "Virginia Sherman",
            "gender": "female",
            "gradePointAverage": 3,
            "hoursStudied": 28,
            "favColor": "black"
          },
          {
            "_id": "5dc83cc56fcc73a8346fcf6a",
            "age": 25,
            "balance": "$4,634.10",
            "eyeColor": "brown",
            "name": "Brown Shields",
            "gender": "male",
            "gradePointAverage": 16,
            "hoursStudied": 26,
            "favColor": "yellow"
          }
        
      ];
      var col = this.columnParser(data);
     var pie = this.pieParser(data);
    var scat = this.scatterParser(data);
    return (

        <div>
        <h1>Hello user !</h1>

        <Chart  width={300}
        height={300}
        chartType="ColumnChart"
        loader={<div>Loading Chart</div>}
        data={col}
        options={{
          title: 'Population of Largest U.S. Cities',
          chartArea: { width: '30%' },
          hAxis: {
            title: 'Total Population',
            minValue: 0,
          },
          vAxis: {
            title: 'City',
          },
        }}
        legendToggle/>

<Chart
  width={'500px'}
  height={'300px'}
  chartType="PieChart"
  loader={<div>Loading Chart</div>}
  data=/*{[
    ['Task', 'Hours per Day'],
    ['Work', 11],
    ['Eat', 2],
    ['Commute', 2],
    ['Watch TV', 2],
    ['Sleep', 7],
  ]}*/{pie}
  options={{
    title: 'My Daily Activities',
  }}
  rootProps={{ 'data-testid': '1' }}
/>
<Chart
  width={'500px'}
  height={'300px'}
  chartType="ScatterChart"
  loader={<div>Loading Chart</div>}
  data=/*{[
    ['Diameter', 'Age'],
    [8, 37],
    [4, 19.5],
    [11, 52],
    [4, 22],
    [3, 16.5],
    [6.5, 32.8],
    [14, 72],
  ]}*/ {scat}
  options={{
    title: 'Age of sugar maples vs. trunk diameter, in inches',
    hAxis: { title: 'Diameter' },
    vAxis: { title: 'Age' },
    legend: 'none',
    trendlines: { 0: {} },
  }}
  rootProps={{ 'data-testid': '1' }}
/>
        </div>

    );

}
    

    

}

export default App2;
