import React from 'react'
import Chart from 'react-google-charts'

class App2 extends React.Component {


render(){
    var pie = new Array(7);
    var bluesum,  brownsum, blacksum, greensum, redsum, yellowsum = 0;
    //var histo = new Array(6);
    var i; var j;
    var data = [
        {
          "age": 38,
          "eyeColor": "brown",
          "name": "Hart Mccoy",
          "gender": "male",
          "gradePointAverage": 8,
          "favColor": "yellow"
        },
        {
          "age": 26,
          "eyeColor": "green",
          "name": "Dorthy Wright",
          "gender": "female",
          "gradePointAverage": 19,
          "favColor": "blue"
        },
        {
          "age": 40,
          "eyeColor": "green",
          "name": "Butler White",
          "gender": "male",
          "gradePointAverage": 14,
          "favColor": "blue"
        },
        {
          "age": 39,
          "eyeColor": "blue",
          "name": "Gena Rosales",
          "gender": "female",
          "gradePointAverage": 19,
          "favColor": "yellow"
        },
        {
          "age": 21,
          "eyeColor": "blue",
          "name": "Brittany Leblanc",
          "gender": "female",
          "gradePointAverage": 6,
          "favColor": "red"
        },
        {
          "age": 28,
          "eyeColor": "green",
          "name": "Arline Hays",
          "gender": "female",
          "gradePointAverage": 19,
          "favColor": "brown"
        }
      ];
      pie[6] = new Array(2);

     for(i=0; i<6; i++){
         pie[i] = new Array(2);

         if(data[i].favColor === "blue"){
             bluesum++;
         }
         if(data[i].favColor === "brown"){
            brownsum++;
        }
        if(data[i].favColor === "black"){
            blacksum++;
        }
        if(data[i].favColor === "green"){
            greensum++;
        }
        if(data[i].favColor === "red"){
            redsum++;
        }
        if(data[i].favColor === "yellow"){
            yellowsum++;
        }
     }
     pie[0][0] = "Color"
     pie[0][1] = "Number of people which has this color as favorite";
     
     pie[1][0] = "brown"
     pie[1][1] = brownsum;

     pie[2][0] = "black"
     pie[2][1] = blacksum;

     pie[3][0] = "green"
     pie[3][1] = greensum;

     pie[4][0] = "red"
     pie[4][1] = redsum;

     pie[5][0] = "yellow"
     pie[5][1] = yellowsum;

     pie [6][0] = "blue";
     pie[6][1] = bluesum;

    return (

        <div>
        <h1>Hello user !</h1>

        <Chart  width={300}
        height={300}
        chartType="ColumnChart"
        loader={<div>Loading Chart</div>}
        data={[
          ['City', '2010 Population'],
          ['New York City, NY', 8175000],
          ['Los Angeles, CA', 3792000],
          ['Chicago, IL', 2695000],
          ['Houston, TX', 2099000],
          ['Philadelphia, PA', 1526000],
        ]}
        options={{
          title: 'Population of Largest U.S. Cities',
          chartArea: { width: '30%' },
          hAxis: {
            title: 'Total Population',
            minValue: 0,
          },
          vAxis: {
            title: 'City',
          },
        }}
        legendToggle/>

<Chart
  width={'500px'}
  height={'300px'}
  chartType="PieChart"
  loader={<div>Loading Chart</div>}
  data=/*{[
    ['Task', 'Hours per Day'],
    ['Work', 11],
    ['Eat', 2],
    ['Commute', 2],
    ['Watch TV', 2],
    ['Sleep', 7],
  ]}*/{pie}
  options={{
    title: 'My Daily Activities',
  }}
  rootProps={{ 'data-testid': '1' }}
/>
        </div>

    );

}
    

    

}

export default App2;
